import React from 'react';

import {
  GroupContainer,
  FormInputContainer,
  FormLabelContainer
} from './form-input.styles';

const FormInput = ({handleChange, label, ...otherProps}) => (
  <GroupContainer>
    <FormInputContainer
      onChange={handleChange} 
      {...otherProps} 
    />
    {
      label ? (
        <FormLabelContainer length={otherProps.value.length}>
          {label}
        </FormLabelContainer>
      ) : null
    }
  </GroupContainer>
)


export default FormInput;
